import { reactive } from "@vue/reactivity";
import BaseHeading from "./UI/BaseHeading.vue";
import BaseSelect from "./UI/BaseSelect.vue";
import { computed } from "@vue/runtime-core";
export default {
  __name: 'ChooseDelivery',
  emits: ["onSelectDeliveryOption"],

  setup(__props, {
    expose,
    emit
  }) {
    expose();
    const DELIVERY_OPTIONS = [{
      id: 1,
      name: "Pickup",
      type: "pickup",
      price: 0
    }, {
      id: 1,
      name: "Delivery",
      type: "delivery",
      price: 10
    }];
    const deliveryOptions = DELIVERY_OPTIONS;
    const selectedDiveryOption = reactive({});

    const selectDelivery = deliveryOption => {
      Object.assign(selectedDiveryOption, deliveryOption);
      emit("onSelectDeliveryOption", deliveryOption);
    };

    const price = computed(() => selectedDiveryOption.price === 0 ? "It's free" : "Price: $" + selectedDiveryOption.price);
    const __returned__ = {
      DELIVERY_OPTIONS,
      emit,
      deliveryOptions,
      selectedDiveryOption,
      selectDelivery,
      price,
      reactive,
      BaseHeading,
      BaseSelect,
      computed
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

};
